package fundamentos;

public class Aritmeticos {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println(2 + 3);
		
		var x = 3.21;
		double y = 2.45;
		
		System.out.println(x + y);
		System.out.println(x - y);
		System.out.println(x * y);
		System.out.println(x / y);
		
		int a = 21;
		int b = 45;
		
		System.out.println(a + b);
		System.out.println(a - b);
		System.out.println(a * b);
		System.out.println(a / (double) b);
		
		System.out.println(a % b);
	
	
	
	
	}

}
